import React, { useEffect, useState } from "react";
import { api } from "../api/axios";
import CourseCard from "../components/CourseCard";
import Loader from "../components/Loader";
import ErrorBox from "../components/ErrorBox";

export default function Home() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchCourses() {
      try {
        setLoading(true);
        setError("");
        const res = await api.get("/courses");
        setCourses(res.data);
      } catch (err) {
        setError("Failed to load courses. Please check API server.");
      } finally {
        setLoading(false);
      }
    }

    fetchCourses();
  }, []);

  return (
    <div>
      <section className="hero">
        <h1>Welcome to Parikshe Learning Platform</h1>
        <p>
          Learn smart with notes, videos, test series and full SSLC preparation.
        </p>
        <div className="hero-actions">
          <a href="/sslc" className="btn btn-primary">Explore SSLC</a>
          <a href="/contact" className="btn btn-outline">Contact Us</a>
        </div>
      </section>

      <section className="section">
        <h2 className="section-title">Courses</h2>

        {loading && <Loader text="Fetching courses..." />}
        {error && <ErrorBox message={error} />}

        {!loading && !error && (
          <div className="grid">
            {courses.map((c) => (
              <CourseCard key={c.id} course={c} />
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
